package io.choerodon.base.infra.dto;import io.swagger.annotations.ApiModelProperty;import javax.persistence.GeneratedValue;import javax.persistence.GenerationType;import javax.persistence.Id;import javax.persistence.Table;/** * FD_ROUTE_RULE DTO * * @author pengyuhua * @date 2019/10/25 */@Table(name = "fd_route_rule")public class RouteRuleDTO {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @ApiModelProperty(value = "主键ID")    private Long id;    @ApiModelProperty(value = "路由名称/必填")    private String name;    @ApiModelProperty(value = "路由编码(使用生成的UUID)/必填")    private String code;    @ApiModelProperty(value = "路由描述/选填")    private String description;    public Long getId() {        return id;    }    public RouteRuleDTO setId(Long id) {        this.id = id;        return this;    }    public String getName() {        return name;    }    public RouteRuleDTO setName(String name) {        this.name = name;        return this;    }    public String getCode() {        return code;    }    public RouteRuleDTO setCode(String code) {        this.code = code;        return this;    }    public String getDescription() {        return description;    }    public RouteRuleDTO setDescription(String description) {        this.description = description;        return this;    }}