mapper.select:
select DISTINCT im.id, im.code, (case when imt.name is not null then imt.name else im.name end ) name, im.fd_level, im.parent_id, im.type, im.sort, im.icon, im.h_quick_index, (case when im.route is null then '' else im.route end) route from iam_menu im
INNER JOIN hiam_label_rel hlr ON hlr.data_type = #{menuLabelDataType} AND hlr.data_id = im.id INNER JOIN iam_label il ON hlr.label_id = il.id AND il.name IN
#{label}
INNER JOIN (SELECT fcm.MENU_CODE FROM fd_project_map_category fpmc INNER JOIN fd_project_category fpc ON fpc.id = fpmc.CATEGORY_ID INNER JOIN fd_category_menu fcm on fcm.CATEGORY_CODE=fpc.`CODE` WHERE fpmc.PROJECT_ID = #{projectId}) as temp on temp.MENU_CODE = im.code
left join iam_menu_tl imt on imt.id = im.id and imt.lang = #{lang} where im.type in ('root', 'dir', 'menu', 'link', 'inner-link', 'window') and im.h_enabled_flag = 1 and im.h_virtual_flag = 0 and (im.h_tenant_id = 0 or im.h_tenant_id = #{tenantId})
AND (SELECT COUNT(1) FROM hiam_label_rel hlr INNER JOIN iam_label il ON hlr.label_id = il.id WHERE hlr.data_type = #{menuLabelDataType} AND hlr.data_id = im.id AND il.name IN
#{label}
) = #{labelsSize}
and exists ( select 1 from iam_menu im_ps join iam_role ir on ( ir.fd_level = im_ps.fd_level and ir.is_enabled = 1 and ir.id in
#{roleId}
) left join iam_role_permission irp on (irp.permission_id = im_ps.id and irp.role_id = ir.id and irp.type = 'PS') where im_ps.h_enabled_flag = 1 and im_ps.type = 'ps'
and im.fd_level = ir.fd_level
and ( ir.code in ('role/site/default/administrator', 'role/organization/default/administrator') or (irp.h_create_flag = 'Y' or irp.h_inherit_flag = 'Y') )
and (im_ps.h_level_path like concat(im.h_level_path, '|%')) )

select DISTINCT im.id, im.code, (case when imt.name is not null then imt.name else im.name end ) name, im.fd_level, im.parent_id, im.type, im.sort, im.icon, im.h_quick_index, (case when im.route is null then '' else im.route end) route from iam_menu im
INNER JOIN hiam_label_rel hlr ON hlr.data_type = #{menuLabelDataType} AND hlr.data_id = im.id INNER JOIN iam_label il ON hlr.label_id = il.id AND il.name IN
#{label}
left join iam_menu_tl imt on imt.id = im.id and imt.lang = #{lang} where im.type in ('root', 'dir', 'menu', 'link', 'inner-link', 'window') and im.h_enabled_flag = 1 and im.h_virtual_flag = 0
AND (SELECT COUNT(1) FROM hiam_label_rel hlr INNER JOIN iam_label il ON hlr.label_id = il.id WHERE hlr.data_type = #{menuLabelDataType} AND hlr.data_id = im.id AND il.name IN
#{label}
) = #{labelsSize}
